import org.apache.hadoop.fs.Path
import org.apache.hadoop.fs.FileSystem
import java.net.URI

    val numFiles = 1000000
    val maxFilesInAPartition = 10
    val basePath = s"s3://dev.canopydata.com/ipp-test-tables-${numFiles}-files"
    (0 to math.log10(numFiles).toInt).foreach(factor => {
      val numParititons = (numFiles / math.pow(10, factor)).toInt
      val numFilesInPartiton = numFiles / numParititons
      println(s"numPartitions = ${numParititons}; numFilesInpartiton = ${numFilesInPartiton}")

      val files = (1 to numParititons).map(partitionNumber => {
          (1 to (numFilesInPartiton)).map( fileNum => {
            s"${basePath}/${numParititons}_partitions/${partitionNumber}/${fileNum}"
          })
      }).flatten

      val shuffledFiles = scala.util.Random.shuffle(files)

      sc.parallelize(shuffledFiles, 1000).map( path => {
          val fs = FileSystem.get(new URI("s3://dev.canopydata.com/"), new org.apache.hadoop.conf.Configuration())
          fs.create(new Path(path), true).close()
          1
      }).count()
    })
